mapper.insert:
INSERT INTO pds
	 (
	   item_name,
	   description
	 )
VALUES
	 (
	   #{itemName},
	   #{description}
	 )

mapper.update:
UPDATE pds
   SET item_name = #{itemName},
   	   description = #{description}
 WHERE item_id = #{itemId}

mapper.insert:
INSERT INTO pds_attach
	 (
	   fullname,
	   item_id
	 ) 
VALUES 
	 (
	   #{fullName},
	   #{itemId}
	 )

mapper.update:
UPDATE pds_attach
   SET down_cnt = down_cnt + 1
 WHERE fullname = #{fullname}

UPDATE pds
   SET view_cnt = view_cnt + 1
 WHERE item_id = #{itemId}
